(()=>{"use strict";class t{constructor(t){this.carouselContainer=t,this.slideQuantity=this.countSlides(),this.currentImageIndex=this.initialiseCurrentImageIndex(),this.fillNavigationBar(),this.activateRightArrowButton()}set currentImageIndex(t){null!==t&&(this._currentImageIndex=(t%this.slideQuantity+this.slideQuantity)%this.slideQuantity)}get currentImageIndex(){return this._currentImageIndex}countSlides(){return this.carouselContainer.querySelectorAll(".slide-list img").length}initialiseCurrentImageIndex(){return this.slideQuantity?0:null}fillNavigationBar(){const t=this.getNavigationBar();for(let e=0;e<this.slideQuantity;e+=1)t.appendChild(this.makeNavigationDot(e))}getNavigationBar(){return this.carouselContainer.querySelector(".navigation")}makeNavigationDot(t){const e=document.createElement("button");return e.classList.add("dot"),0===t&&e.classList.add("active"),e}activateRightArrowButton(){this.getArrowButton("right").addEventListener("click",(()=>this.incrementSlide(1)))}getArrowButton(t){return this.carouselContainer.querySelector(`button.arrow.${t}`)}incrementSlide(t){this.currentImageIndex+=t,this.goToSlide(this.currentImageIndex)}goToSlide(t){this.displaySlide(t)}displaySlide(t){const e=-100*t+"%";this.getSlideListDiv().style.left=e}getSlideListDiv(){return document.querySelector(".slide-list")}}class e{static loadCarousels(){e.getCarouselContainerList().forEach((t=>e.makeCarousel(t)))}static getCarouselContainerList(){return document.querySelectorAll(".carousel")}static makeCarousel(e){return new t(e)}}document.addEventListener("DOMContentLoaded",e.loadCarousels)})();