(()=>{"use strict";class e{constructor(e){this.carouselContainer=e,this.slideQuantity=this.countSlides(),console.log(this.slideQuantity)}countSlides(){return this.carouselContainer.querySelectorAll(".slide-list img").length}}class t{static loadCarousels(){t.getCarouselContainerList().forEach((e=>t.makeCarousel(e)))}static getCarouselContainerList(){return document.querySelectorAll(".carousel")}static makeCarousel(t){return new e(t)}}document.addEventListener("DOMContentLoaded",t.loadCarousels)})();