(()=>{"use strict";class t{constructor(t){this.carouselContainer=t,this.slideQuantity=this.countSlides(),this.currentImageIndex=this.initialiseCurrentImageIndex(),this.makeNavigationDots()}countSlides(){return this.carouselContainer.querySelectorAll(".slide-list img").length}initialiseCurrentImageIndex(){return this.slideQuantity?0:null}makeNavigationDots(){const t=this.getNavigationBar();console.log(t),t.style.outlineColor="red"}getNavigationBar(){return this.carouselContainer.querySelector(".navigation")}}class e{static loadCarousels(){e.getCarouselContainerList().forEach((t=>e.makeCarousel(t)))}static getCarouselContainerList(){return document.querySelectorAll(".carousel")}static makeCarousel(e){return new t(e)}}document.addEventListener("DOMContentLoaded",e.loadCarousels)})();