(()=>{"use strict";class t{constructor(t){this.carouselContainer=t,this.slideQuantity=this.countSlides(),this.currentSlideIndex=this.initialiseCurrentImageIndex(),this.fillNavigationBar(),this.activateRightArrowButton()}set currentSlideIndex(t){null!==t&&(this._currentSlideIndex=(t%this.slideQuantity+this.slideQuantity)%this.slideQuantity)}get currentSlideIndex(){return this._currentSlideIndex}countSlides(){return this.carouselContainer.querySelectorAll(".slide-list img").length}initialiseCurrentImageIndex(){return this.slideQuantity?0:null}fillNavigationBar(){const t=this.getNavigationBar();for(let e=0;e<this.slideQuantity;e+=1)t.appendChild(this.makeNavigationDot(e))}getNavigationBar(){return this.carouselContainer.querySelector(".navigation")}makeNavigationDot(t){const e=document.createElement("button");return e.classList.add("dot"),0===t&&e.classList.add("active"),e}activateRightArrowButton(){this.getArrowButton("right").addEventListener("click",(()=>this.incrementSlide(1)))}getArrowButton(t){return this.carouselContainer.querySelector(`button.arrow.${t}`)}incrementSlide(t){this.currentSlideIndex+=t,this.goToCurrentSlide()}goToCurrentSlide(){this.displayCurrentSlide(),this.updateActiveNavigationDot()}displayCurrentSlide(){const t=this.getSlideListDiv(),e=-100*this.currentSlideIndex+"%";t.style.left=e}getSlideListDiv(){return document.querySelector(".slide-list")}updateActiveNavigationDot(){const t=this.getNavigationBar();t.querySelector(".active").classList.remove("active"),t.childNodes[this.currentSlideIndex].classList.add("active")}}class e{static loadCarousels(){e.getCarouselContainerList().forEach((t=>e.makeCarousel(t)))}static getCarouselContainerList(){return document.querySelectorAll(".carousel")}static makeCarousel(e){return new t(e)}}document.addEventListener("DOMContentLoaded",e.loadCarousels)})();