(()=>{"use strict";class t{constructor(t){this.carouselContainer=t,this.slideQuantity=this.countSlides(),this.currentImageIndex=this.initialiseCurrentImageIndex(),this.fillNavigationBar()}countSlides(){return this.carouselContainer.querySelectorAll(".slide-list img").length}initialiseCurrentImageIndex(){return this.slideQuantity?0:null}fillNavigationBar(){const t=this.getNavigationBar();for(let e=0;e<this.slideQuantity;e+=1)t.appendChild(this.makeNavigationDot(e))}getNavigationBar(){return this.carouselContainer.querySelector(".navigation")}makeNavigationDot(t){const e=document.createElement("button");return e.classList.add("dot"),0===t&&e.classList.add("active"),e}}class e{static loadCarousels(){e.getCarouselContainerList().forEach((t=>e.makeCarousel(t)))}static getCarouselContainerList(){return document.querySelectorAll(".carousel")}static makeCarousel(e){return new t(e)}}document.addEventListener("DOMContentLoaded",e.loadCarousels)})();